mapper.select:
select teacher_seq as no, teacher_id as id, teacher_name as name, CONCAT('/file/teacher/',teacher_img) as imageFile,
       teacher_status as status, teacher_regdt as regDt
from teacher_info where teacher_name like '%${keyword}%'
order by ${orderType} ${order}
limit 10 offset #{offset}

mapper.update:
update teacher_info
set teacher_pwd = #{pwd}

    , teacher_name = #{name}


    , teacher_img = #{filename}

where teacher_id = #{teacherId}

UPDATE class_info
SET
    class_seq = #{classNo}
    ,class_name = #{data.name}
    ,class_grade = #{data.grade}
    ,class_days = #{data.days}
    ,class_opendt = #{data.opendt}
    ,class_closedt = #{data.closedt}
    ,class_starttime = #{data.starttime}
    ,class_endtime = #{data.endtime}
    ,class_teacher_seq = #{data.teacherNo}
WHERE class_seq = #{classNo};
